"use client";
import Image from "next/image";
import { useState, useEffect, useRef } from "react";
import { FaStar, FaLinkedin } from "react-icons/fa";

const testimonials = [
	{
		name: "Priya Sharma",
		role: "Marketing Lead, FinTech Co.",
		photo: "data:image/jpeg;base64,",
		quote:
			"ADmyBRAND AI Suite transformed our marketing! Campaigns are smarter, faster, and our ROI has never been better.",
		rating: 5,
		linkedin: "https://linkedin.com/in/priyasharma",
		companyLogo: "data:image/png;base64,",
	},
	{
		name: "Alex Kim",
		role: "Founder, StartupHub",
		photo: "data:image/jpeg;base64,",
		quote:
			"The automation and analytics features are a game changer. Our team collaborates seamlessly and saves hours every week.",
		rating: 5,
		linkedin: "https://linkedin.com/in/alexkim",
		companyLogo: "data:image/png;base64,",
	},
	{
		name: "Sara Lee",
		role: "CMO, RetailX",
		photo: "data:image/jpeg;base64,",
		quote:
			"We love the content generation tools! Our creatives are more engaging and our workflow is so much smoother.",
		rating: 4,
		linkedin: "https://linkedin.com/in/saralee",
		companyLogo: "data:image/png;base64,",
	},
];

export default function TestimonialsSection() {
	const [current, setCurrent] = useState(0);
	const [paused, setPaused] = useState(false);
	const timeoutRef = useRef<NodeJS.Timeout | null>(null);

	// Autoplay logic
	useEffect(() => {
		if (!paused) {
			timeoutRef.current = setTimeout(() => {
				setCurrent((prev) => (prev + 1) % testimonials.length);
			}, 5000);
		}
		return () => {
			if (timeoutRef.current) clearTimeout(timeoutRef.current);
		};
	}, [current, paused]);

	// Keyboard navigation
	useEffect(() => {
		const handleKey = (e: KeyboardEvent) => {
			if (e.key === "ArrowLeft") prev();
			if (e.key === "ArrowRight") next();
		};
		window.addEventListener("keydown", handleKey);
		return () => window.removeEventListener("keydown", handleKey);
		// eslint-disable-next-line
	}, [current]);

	const prev = () =>
		setCurrent((current - 1 + testimonials.length) % testimonials.length);
	const next = () => setCurrent((current + 1) % testimonials.length);

	return (
		<section
			className="py-20 bg-gradient-to-b from-gray-50 to-white px-4"
			aria-label="Testimonials"
		>
			<div className="max-w-3xl mx-auto text-center mb-12">
				<h2 className="text-3xl sm:text-4xl font-bold mb-4 text-blue-700">
					What Our Customers Say
				</h2>
				<p className="text-gray-600 text-lg">
					Real stories from teams using ADmyBRAND AI Suite.
				</p>
			</div>
			<div
				className="max-w-xl mx-auto relative"
				onMouseEnter={() => setPaused(true)}
				onMouseLeave={() => setPaused(false)}
				tabIndex={0}
				aria-roledescription="carousel"
				aria-label="Customer testimonials carousel"
			>
				<div
					className="bg-white rounded-2xl shadow-xl p-8 flex flex-col items-center transition-all duration-500 ease-in-out"
					key={testimonials[current].name}
					style={{
						animation: "fade-in 0.5s",
					}}
					aria-live="polite"
				>
					<div className="relative flex flex-col items-center w-full">
						<Image
							src={testimonials[current].photo}
							alt={testimonials[current].name}
							width={80}
							height={80}
							className="rounded-full mb-4 border-4 border-blue-100 object-cover"
						/>
						{testimonials[current].companyLogo && (
							<Image
								src={testimonials[current].companyLogo}
								alt={testimonials[current].role}
								width={48}
								height={48}
								className="absolute -bottom-4 right-1 rounded-lg bg-white border border-gray-200 shadow"
							/>
						)}
					</div>
					<div className="flex items-center mt-6 mb-2">
						{Array.from({ length: 5 }).map((_, i) => (
							<FaStar
								key={i}
								className={`text-lg mr-1 ${
									i < testimonials[current].rating
										? "text-yellow-400"
										: "text-gray-200"
								}`}
								aria-hidden="true"
							/>
						))}
						<span className="sr-only">
							{testimonials[current].rating} out of 5 stars
						</span>
					</div>
					<p className="text-xl text-gray-800 italic mb-4 animate-fade-in">
						"{testimonials[current].quote}"
					</p>
					<div className="flex flex-col items-center gap-1">
						<div className="font-semibold text-blue-700 flex items-center gap-2">
							{testimonials[current].name}
							<a
								href={testimonials[current].linkedin}
								target="_blank"
								rel="noopener noreferrer"
								aria-label={`LinkedIn profile of ${testimonials[current].name}`}
								className="text-blue-600 hover:text-blue-800"
							>
								<FaLinkedin />
							</a>
						</div>
						<div className="text-gray-500 text-sm">
							{testimonials[current].role}
						</div>
					</div>
				</div>
				{/* Carousel Controls */}
				<div className="flex justify-center mt-6 gap-4">
					<button
						aria-label="Previous testimonial"
						onClick={prev}
						className="w-10 h-10 rounded-full bg-blue-50 hover:bg-blue-100 flex items-center justify-center text-blue-600 text-xl transition"
					>
						&#8592;
					</button>
					<button
						aria-label="Next testimonial"
						onClick={next}
						className="w-10 h-10 rounded-full bg-blue-50 hover:bg-blue-100 flex items-center justify-center text-blue-600 text-xl transition"
					>
						&#8594;
					</button>
				</div>
				{/* Dots */}
				<div
					className="flex justify-center mt-4 gap-2"
					aria-label="Testimonial navigation dots"
				>
					{testimonials.map((_, idx) => (
						<button
							key={idx}
							aria-label={`Go to testimonial ${idx + 1}`}
							aria-current={idx === current}
							className={`inline-block w-3 h-3 rounded-full transition-all duration-200 border ${
								idx === current
									? "bg-blue-600 border-blue-600"
									: "bg-blue-200 border-blue-200"
							}`}
							onClick={() => setCurrent(idx)}
						/>
					))}
				</div>
			</div>
			<style jsx>{`
				@keyframes fade-in {
					from {
						opacity: 0;
						transform: translateY(16px);
					}
					to {
						opacity: 1;
						transform: translateY(0);
					}
				}
				.animate-fade-in {
					animation: fade-in 0.5s;
				}
			`}</style>
		</section>
	);
}
